import React from 'react';
import karpuram from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Assets/karpuram.png';
import './Items.css';
import {useState} from 'react';
const Items=(props)=>{
    const[update,setUpdate]=useState([]);
      const handleClick = (id) => {
             setUpdate((prevState) => ({
                 ...prevState, // Preserve existing states
                 [id]: !prevState[id], // Toggle the state for this specific id
             }));
         };
         const handleDelete = async (id) => {
          preventDefault();
          try {
            const response = await fetch("http://localhost:8080/product/delete{id}", {
              method: "Delete",
              headers: { "Content-Type": "application/json" },
            });
 
            if (response.ok) {
              alert("Product deleted successfully!");
              // Reset form
            } else {
              alert("Failed to delete product.");
 
            }
          } catch (error) {
            console.error("Error:", error);
          }
        };
 
    return(
        <>
          <div className="card" style={{ width: "18rem" }}>
            <img src={props.image} className="card-img-top" alt="..." />
            <div className="card-body">
                <h5 className="card-text">{props.name}</h5>
             <h6 className="card-title">Price in grams  : {props.price}</h6>
             {update[props.id] && <input type="number" placeholder="search"/>}
              <a href="#" className="button" onClick={()=>
              {if(props.action==="Modify Price")
                  handleClick(props.id)
                else if(props.action==="Delete")
                  handleDelete(props.id)
                else
                   console.log('Deepak');

                  }

                  }>

                {props.action}
              </a>
            </div>
          </div>

        </>
        )}

export default Items;
