import React from 'react';
import Data from './Data.json';
import Items from './Items.jsx';
import './all.css';
export const Update=()=>{
    return(
        <div className="Deepak">
          {Data.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} action="Modify Price"/>
              })}
          </div>
)
}

export const All=()=>{

     useEffect(() => {
            fetch("http://localhost:3000/api/items") // Fetching from backend
                .then((response) => response.json())
                .then((data) => setItems(data))
                .catch((error) => console.error("Error fetching items:", error));
        }, []);
    return(
        <div className="Deepak">
          {Data.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} action="Add to Cart"/>
              })}
          </div>
)
}
