import React from 'react';
import Data from './Data.json';
import Items from './Items.jsx';
import './all.css';
import {useState,useEffect} from 'react';
export const Update=()=>{
    const [items, setItems] = useState([]);
            useEffect(() => {
                fetch("http://localhost:8080/product/all") // Fetching from backend
                    .then((response) => response.json())
                    .then((data) => setItems(data))
                    .catch((error) => console.error("Error fetching items:", error));
                }, []);
    return(
        <div className="product-list">
          {Data.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Modify Price"/>
              })}
          </div>
)
}

export const All=()=>{

    const [items, setItems] = useState([]);
        useEffect(() => {
            fetch("http://localhost:8080/product/all") // Fetching from backend
                .then((response) => response.json())
                .then((data) => setItems(data))
                .catch((error) => console.error("Error fetching items:", error));
            }, []);
    return(
        <div className="product-list">
          {items.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Add to Cart"/>
              })}
          </div>
)
}
export  const Add=()=>{
    return(
        <div className="form">
         <div className="form-inner">
           <div className="form-floating mb-3">
             <input
               type="text"
               className="form-control"
               id="floatingInput"
               placeholder="name@example.com"
             />
             <label htmlFor="floatingInput">Name of the product</label>
           </div>
           <div className="form-floating">
             <input
               type="text"
               className="form-control"
               id="floatingPassword"
               placeholder="Password"
             />
             <label htmlFor="floatingInput">Image Url</label>
           </div>
           <div className="form-floating">
             <select className="form-select" aria-label="Default select example">
                        <option selected="">Open this select menu</option>
                        <option value={1}>price in grams</option>
                        <option value={2}>price per one item</option>
                        <option value={3}>price in Kilos</option>
             </select>
            </div>
         </div>

       </div>
)
};
