import React from 'react';
import karpuram from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Assets/karpuram.png';
import './Items.css';
import {useState} from 'react';
const Items=(props)=>{
    const[update,setUpdate]=useState([]);
      const handleClick = (id) => {
             setUpdate((prevState) => ({
                 ...prevState, // Preserve existing states
                 [id]: !prevState[id], // Toggle the state for this specific id
             }));
         };
    return(
        <>
          <div className="card" style={{ width: "18rem" }}>
            <img src='https://unsplash.com/photos/a-woman-sitting-at-a-table-using-a-laptop-computer-ewruWCpmAXI' className="card-img-top" alt="..." />
            <div className="card-body">
                <h5 className="card-text">{props.name}</h5>
             <h6 className="card-title">Price in grams  : {props.price}</h6>
             {update[props.id] && <input type="number" placeholder="search"/>}
              <a href="#" className="btn btn-primary" onClick={()=>{if(props.action==="Modify Price")
                  handleClick(props.id)

                  }

                  }>

                {props.action}
              </a>
            </div>
          </div>

        </>
        )}

export default Items;
