import React from 'react';
import Data from './Data.json';
import Items from './Items.jsx';
import './all.css';
import {useState,useEffect} from 'react';
export const Update=()=>{
    const [items, setItems] = useState([]);
            useEffect(() => {
                fetch("http://localhost:8080/product/all") // Fetching from backend
                    .then((response) => response.json())
                    .then((data) => setItems(data))
                    .catch((error) => console.error("Error fetching items:", error));
                }, []);
    return(
        <div className="product-list">
          {Data.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Modify Price"/>
              })}
          </div>
)
}

export const All=()=>{

    const [items, setItems] = useState([]);
        useEffect(() => {
            fetch("http://localhost:8080/product/all") // Fetching from backend
                .then((response) => response.json())
                .then((data) => setItems(data))
                .catch((error) => console.error("Error fetching items:", error));
            }, []);
    return(
        <div className="product-list">
          {items.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Add to Cart"/>
              })}
          </div>
)
}

 export const Add = () => {
     const[values,setValues]=useState({
         name:'',
         imageUrl:'',
         Category:'',
         price:""
         })
     const handleChange=(e)=>{
         setValues({...values,[e.target.name]:[e.target.value]})
         }
     const handleSubmit=(e)=>{
         e.preventDefault()
         console.log(values)
         }


            return(
                <div className="form">
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="name">Product Name</label>
                        <input type="text" placeholder="Enter Product Name" name="name" onChange={(e)=>handleChange(e)}/>

                        <label htmlFor="imageUrl">Image Url</label>
                        <input type="text" placeholder="Enter image Url" name="imageUrl" onChange={(e)=>handleChange(e)}/>

                        <label htmlFor="Category">category</label>
                        <select name="Category" id="Category" onChange={(e)=>handleChange(e)}>
                            <option value="price in grams">price in grams</option>
                            <option value="price per item">price per item</option>
                        </select>

                        <label htmlFor="price">Price</label>
                        <input type="number" placeholder="Enter price of product" name="price" onChange={(e)=>handleChange(e)}/>

                      <button type="submit" className="btn btn-dark">
                        Submit
                      </button>


                    </form>
                </div>

                )
            };
