 import React from 'react';
 import './admin.css';
import {useEffect} from 'react';
 import Update from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Update.jsx';
 import Add from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Add.jsx';
  import Delete from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Delete.jsx';
  import Quantity from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Quantity.jsx';
 import{Link} from  'react';
 import Data from './Data.json';
 import {useState} from 'react';
 import karpuram from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Assets/karpuram.png';

const Admin=()=>{
    const[all,setAll]=useState(false);
    const[search,setSearch]=useState(20);
    const[tempPrice,setTempPrice]=useState("");
    const[price,setPrice]=useState(true)
    const[items,setItems]=useState([]);
     useEffect(() => {
        fetch("/Data.json")
          .then((response) => response.json())
          .then((items) => setAll(Data))
          .catch((error) => console.error("Error fetching data:", error));
      }, []);


    const handleUpdatePrice = () => {
        if (tempPrice.trim() !== "") {
          setSearch(tempPrice);
          setTempPrice("");
          // Update price when Modify Price is clicked
        }
        setAll(false); // Hide input field after update
      };                                                                                                                                                                                                                                         ;
    const All=()=>{
             return(
                 <div className="all">
                     {all.map((item)=>(

                     <div className="card" style={{ width: "18rem" }}>
                         <img src={karpuram} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">Price in grams  : {all.price}</h5>

                                {all && (
                                            <input type="number"
                                             placeholder="Enter price"
                                             value={tempPrice}
                                              onChange={(e) => setTempPrice(e.target.value)}
                                            />
                                          )}
                                <a href="#" className="btn btn-primary"
                                onClick={()=>{if(all)
                                    {
                                       handleUpdatePrice();

                                        }
                                    else
                                       setAll(true);
                                       }}
                                    >
                                    Modify price
                                </a>
                            </div>
                     </div>
                     ))};
                 </div>
                 )
            };

    return(
        <div>
            <ul className="nav nav-tabs" style={{backgroundColor:"#E6FAF5"}}>
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#" onClick={()=>setAll(!all)}

                    style={{color:"black"}}>
                  All Items

                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#"  style={{color:"black"}}>
                  Update
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" style={{color:"black"}}>
                  Add an Item
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" style={{color:"black"}}>
                  Delete an Item
                </a>
              </li>
            </ul>
            <div>
                {all && <All/>}
         </div>
         </div>


        );
    }
export default Admin;
