import { useState } from "react";
import { TrashIcon } from "@heroicons/react/24/outline";

const Cart = () => {
  const [cartItems, setCartItems] = useState([
    { id: 1, name: "Product A", price: 499, quantity: 1, image: "https://via.placeholder.com/50" },
    { id: 2, name: "Product B", price: 299, quantity: 2, image: "https://via.placeholder.com/50" }
  ]);

  const updateQuantity = (id, change) => {
    setCartItems(cartItems.map(item => 
      item.id === id ? { ...item, quantity: Math.max(1, item.quantity + change) } : item
    ));
  };

  const removeItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-semibold mb-4">Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p className="text-gray-500">Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map(item => (
            <div key={item.id} className="flex justify-between items-center border-b py-3">
              <img src={item.image} alt={item.name} className="w-12 h-12 object-cover rounded" />
              <div className="flex-1 ml-4">
                <h3 className="font-medium">{item.name}</h3>
                <p className="text-gray-500">₹{item.price}</p>
              </div>
              <div className="flex items-center">
                <button onClick={() => updateQuantity(item.id, -1)} className="px-2 bg-gray-200 rounded">-</button>
                <span className="mx-2">{item.quantity}</span>
                <button onClick={() => updateQuantity(item.id, 1)} className="px-2 bg-gray-200 rounded">+</button>
              </div>
              <button onClick={() => removeItem(item.id)} className="ml-4 text-red-500 hover:text-red-700">
                <TrashIcon className="w-5 h-5" />
              </button>
            </div>
          ))}
          <div className="flex justify-between mt-4 text-lg font-semibold">
            <span>Total:</span>
            <span>₹{totalPrice}</span>
          </div>
          <button className="w-full mt-4 bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Proceed to Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default Cart;
