 import React from 'react';
 import './admin.css';
import {useEffect} from 'react';
 import Add from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Add.jsx';
  import Delete from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Delete.jsx';
  import Quantity from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Quantity.jsx';
 import{Link} from  'react';
 import Data from './Data.json';
 import {useState} from 'react';
 import karpuram from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Assets/karpuram.png';
import Items from './Items.jsx';
import {All,Update} from './All';
const Admin=()=>{
    const[active,setActive]=useState("update");

    return(
        <div>
            <ul className="nav nav-tabs" style={{backgroundColor:"#E6FAF5"}}>
              <li className="nav-item">
                <a className={'nav-link ${active==="update"? "active":""}'} aria-current="page" href="#" onClick={()=>setActive("update")}

                    style={{color:"black"}}>
                  Update

                </a>
              </li>
              <li className="nav-item">
                <a className={'nav-link ${active==="update"?"active":""}'} href="#"  style={{color:"black"}} onClick={()=>setActive("all")}>
                  All Items
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link active" href="#" style={{color:"black"}}>
                  Add an Item
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" style={{color:"black"}}>
                  Delete an Item
                </a>
              </li>
            </ul>
            <div className="nav-item">
                {active==="update" && <Update/>}

         </div>
         </div>


        );
    }
export default Admin;
