 import React from 'react';
 import './admin.css';
 import Items from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Items.jsx';
 import Update from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Update.jsx';
 import Add from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Add.jsx';
  import Delete from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Delete.jsx';
  import Quantity from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Admin/Quantity.jsx';
 import{Link} from  'react';
 import Data from './Data.json';
 import {useState} from 'react';
 import karpuram from 'C:/Users/deepa/OneDrive/Desktop/karthik/frontend/src/Components/Assets/karpuram.png';
const Admin=()=>{
    const[all,setAll]=useState(false);
    const All=()=>{
          const Price=()=>{
              return(
              <>
              <input placeholder="Enter price"/>
              </>
              );
              }
        const[price,setPrice]=useState(20);
             return(
                 <div className="all">
                     <div className="card" style={{ width: "18rem" }}>
                         <img src={karpuram} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">Price in grams  : {price}</h5>
                                <p className="card-text">{setPrice && <Price/>}</p>
                                <a href="#" className="btn btn-primary" onClick={()=>setPrice(true)}>
                                    Modify price
                                </a>
                            </div>
                     </div>
                 </div>
                 )
            };

    return(
        <div>
            <ul className="nav nav-tabs" style={{backgroundColor:"#E6FAF5"}}>
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#" onClick={()=>setAll(true)} style={{color:"black"}}>
                  All Items
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#"  style={{color:"black"}}>
                  Update
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" style={{color:"black"}}>
                  Add an Item
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#" style={{color:"black"}}>
                  Delete an Item
                </a>
              </li>
            </ul>
            <div>
                {all && <All/>}
         </div>
         </div>


        );
    }
export default Admin;
