import React from 'react';
import Data from './Data.json';
import Items from './Items.jsx';
import './all.css';
import {useState,useEffect} from 'react';
export const Update=()=>{
    const [items, setItems] = useState([]);
            useEffect(() => {
                fetch("http://localhost:8080/product/all") // Fetching from backend
                    .then((response) => response.json())
                    .then((data) => setItems(data))
                    .catch((error) => console.error("Error fetching items:", error));
                }, []);
    return(
        <div className="product-list">
          {Data.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Modify Price"/>
              })}
          </div>
)
}

export const All=()=>{

    const [items, setItems] = useState([]);
        useEffect(() => {
            fetch("http://localhost:8080/product/all") // Fetching from backend
                .then((response) => response.json())
                .then((data) => setItems(data))
                .catch((error) => console.error("Error fetching items:", error));
            }, []);
    return(
        <div className="product-list">
          {items.map((item,i)=>{
               return <Items key={i} id={item.id} price={item.price} name={item.name} image={item.imageUrl} action="Add to Cart"/>
              })}
          </div>
)
}
export  const Add=()=>{
    return(
       <div className="form">
         <div className="mb-3">
           <label htmlFor="formGroupExampleInput" className="form-label">
             Name of the product
           </label>
           <input
             type="text"
             className="form-control"
             id="formGroupExampleInput"
             placeholder="Example input placeholder"
           />
         </div>
         <div className="mb-3">
           <label htmlFor="formGroupExampleInput2" className="form-label">
             Image Url
           </label>
           <input
             type="text"
             className="form-control"
             id="formGroupExampleInput2"
             placeholder="Another input placeholder"
           />
            <input
                        type="text"
                        className="form-control"
                        id="formGroupExampleInput2"
                        placeholder="Another input placeholder"
                      />
         </div>
       </div>

)
};
